{{Use dmy dates|date=June 2013}} {{Infobox film | name = The Best is Yet to Come | image = | image_size = | caption = | director = | producer = | writer = | narrator = | starring = | music = | cinematography = | editing = | distributor = [[Kroger Babb]] | released = 1951 | runtime = | country = {{USA}} | language = English | budget = | preceded_by = | followed_by = | website = }}
